/*! TextGeometry.min.js */
THREE.TextGeometry = function (text, parameters) {
  parameters = parameters || {};
  var font = parameters.font;
  if (!font) {
    console.error('THREE.TextGeometry: font parameter is required.');
    return;
  }
  var shapes = font.generateShapes(text, parameters.size || 100);
  var geometry = new THREE.ExtrudeGeometry(shapes, {
    depth: parameters.height !== undefined ? parameters.height : 50,
    bevelEnabled: parameters.bevelEnabled !== undefined ? parameters.bevelEnabled : false
  });
  THREE.BufferGeometry.call(this);
  this.copy(geometry);
};
THREE.TextGeometry.prototype = Object.create(THREE.BufferGeometry.prototype);
THREE.TextGeometry.prototype.constructor = THREE.TextGeometry;
